import React, { Component } from "react";

class Index extends Component {
  constructor(props) {
    super(props);
    console.log(
      "CONSTRUCTOR: Data fetching twice, from the server and from the client"
    );
  }

  componentDidMount() {
    console.log("COMPONENTDIDMOUNT: Data fetching from the client only");
  }

  // If we use a functional component, then we need to defined it like this:
  // Page.getInitialProps = async () => { ... }
  static async getInitialProps() {
    console.log(
      "GETINITIALPROPS: Data fetching from the server in the initial page load, then from the client"
    );
  }

  render() {
    console.log("**********RUNNING INDEX COMPONENT**********");
    return <h1>Our Index Page</h1>;
  }
}

export default Index;
